import React, { useState, useRef, useEffect } from 'react';

const WebSpeechRecorder = ({ onTranscriptionComplete, onError }) => {
  const [isRecording, setIsRecording] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [isSupported, setIsSupported] = useState(false);
  const recognitionRef = useRef(null);
  const finalTranscriptRef = useRef('');

  useEffect(() => {
    // Check if Web Speech API is supported
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    
    if (SpeechRecognition) {
      setIsSupported(true);
      
      const recognition = new SpeechRecognition();
      recognition.continuous = true;
      recognition.interimResults = true;
      recognition.lang = 'en-US';
      
      recognition.onstart = () => {
        console.log('🎙️ Speech recognition started');
        setIsRecording(true);
      };

      recognition.onresult = (event) => {
        let interimTranscript = '';
        let finalTranscript = finalTranscriptRef.current;

        for (let i = event.resultIndex; i < event.results.length; i++) {
          const transcriptPart = event.results[i].transcript;
          
          if (event.results[i].isFinal) {
            finalTranscript += transcriptPart + ' ';
          } else {
            interimTranscript += transcriptPart;
          }
        }

        finalTranscriptRef.current = finalTranscript;
        setTranscript(finalTranscript + interimTranscript);
      };

      recognition.onend = () => {
        console.log('🎙️ Speech recognition ended');
        setIsRecording(false);
        
        if (finalTranscriptRef.current.trim()) {
          onTranscriptionComplete({
            transcript: finalTranscriptRef.current.trim(),
            filename: 'voice-recording.wav'
          });
        }
      };

      recognition.onerror = (event) => {
        console.error('Speech recognition error:', event.error);
        setIsRecording(false);
        onError(`Speech recognition error: ${event.error}`);
      };

      recognitionRef.current = recognition;
    } else {
      setIsSupported(false);
      console.warn('Web Speech API not supported');
    }

    return () => {
      if (recognitionRef.current && isRecording) {
        recognitionRef.current.stop();
      }
    };
  }, [onTranscriptionComplete, onError]);

  const startRecording = () => {
    if (!recognitionRef.current || isRecording) return;
    
    finalTranscriptRef.current = '';
    setTranscript('');
    recognitionRef.current.start();
  };

  const stopRecording = () => {
    if (recognitionRef.current && isRecording) {
      recognitionRef.current.stop();
    }
  };

  const toggleRecording = () => {
    if (isRecording) {
      stopRecording();
    } else {
      startRecording();
    }
  };

  if (!isSupported) {
    return (
      <div className="speech-not-supported">
        <div className="error-message">
          <h3>⚠️ Speech Recognition Not Available</h3>
          <p>Your browser doesn't support the Web Speech API.</p>
          <p><strong>Try using:</strong></p>
          <ul>
            <li>Google Chrome (recommended)</li>
            <li>Microsoft Edge</li>
            <li>Safari (limited support)</li>
          </ul>
          <p>You can still upload audio files using the file uploader above.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="web-speech-recorder">
      <div className="recorder-header">
        <h3>🎙️ Live Speech Recognition</h3>
        <p>Click to start speaking directly into your microphone</p>
      </div>

      <div className="recorder-controls">
        <button 
          onClick={toggleRecording}
          className={`record-button ${isRecording ? 'recording' : ''}`}
          disabled={!isSupported}
        >
          {isRecording ? (
            <>
              <span className="record-icon recording">⏹️</span>
              Stop Recording
            </>
          ) : (
            <>
              <span className="record-icon">🎙️</span>
              Start Recording
            </>
          )}
        </button>
      </div>

      {isRecording && (
        <div className="recording-indicator">
          <div className="pulse-dot"></div>
          <span>Recording... Speak clearly into your microphone</span>
        </div>
      )}

      {transcript && (
        <div className="live-transcript">
          <h4>Live Transcript:</h4>
          <div className="transcript-text">
            {transcript}
          </div>
        </div>
      )}
    </div>
  );
};

export default WebSpeechRecorder;
