import React, { useCallback, useState } from 'react';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';

const AudioUploader = ({ onUploadStart, onError }) => {
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);

  const processFile = async (file) => {
    setUploading(true);
    setUploadProgress(0);

    try {
      const formData = new FormData();
      formData.append('audio', file);

      onUploadStart({ filename: file.name, size: file.size });

      const response = await axios.post('/api/summarizer/process', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        },
        onUploadProgress: (progressEvent) => {
          const percentCompleted = Math.round(
            (progressEvent.loaded * 100) / progressEvent.total
          );
          setUploadProgress(percentCompleted);
        }
      });

      if (response.data.success) {
        // Simulate processing delay for demo
        setTimeout(() => {
          onUploadStart({ filename: file.name, processing: true });
        }, 1000);
      }

    } catch (error) {
      console.error('Upload error:', error);
      const errorMessage = error.response?.data?.message || error.message || 'Upload failed';
      onError(errorMessage);
    } finally {
      setUploading(false);
      setUploadProgress(0);
    }
  };

  const onDrop = useCallback(async (acceptedFiles) => {
    if (acceptedFiles.length === 0) return;
    
    const file = acceptedFiles;
    await processFile(file);
  }, []);

  const { getRootProps, getInputProps, isDragActive, fileRejections } = useDropzone({
    onDrop,
    accept: {
      'audio/*': ['.mp3', '.wav', '.m4a', '.ogg'],
      'video/*': ['.mp4', '.mpeg']
    },
    maxSize: 50 * 1024 * 1024, // 50MB
    multiple: false,
    disabled: uploading
  });

  return (
    <div className="uploader-section">
      <div className="upload-instructions">
        <h2>Upload Your Meeting Audio</h2>
        <p>Supported formats: MP3, WAV, M4A, OGG, MP4 (max 50MB)</p>
      </div>

      <div {...getRootProps()} className={`dropzone ${isDragActive ? 'active' : ''} ${uploading ? 'uploading' : ''}`}>
        <input {...getInputProps()} />
        
        {uploading ? (
          <div className="upload-progress">
            <div className="spinner"></div>
            <p>Processing your audio file...</p>
            <div className="progress-bar">
              <div 
                className="progress-fill" 
                style={{ width: `${uploadProgress}%` }}
              ></div>
            </div>
            <p className="progress-text">{uploadProgress}%</p>
          </div>
        ) : (
          <div className="upload-content">
            <div className="upload-icon">üéôÔ∏è</div>
            {isDragActive ? (
              <p><strong>Drop your audio file here</strong></p>
            ) : (
              <>
                <p><strong>Drag & drop an audio file here</strong></p>
                <p>or <button type="button" className="browse-button">browse files</button></p>
              </>
            )}
          </div>
        )}
      </div>

      {fileRejections.length > 0 && (
        <div className="upload-errors">
          {fileRejections.map(({ file, errors }) => (
            <div key={file.path} className="error-message">
              <strong>{file.path}</strong>
              <ul>
                {errors.map(e => <li key={e.code}>{e.message}</li>)}
              </ul>
            </div>
          ))}
        </div>
      )}

      <div className="upload-features">
        <div className="feature-grid">
          <div className="feature">
            <span className="feature-icon">‚ö°</span>
            <h3>Fast Processing</h3>
            <p>AI-powered transcription in seconds</p>
          </div>
          <div className="feature">
            <span className="feature-icon">üéØ</span>
            <h3>Action Items</h3>
            <p>Automatically extract tasks and decisions</p>
          </div>
          <div className="feature">
            <span className="feature-icon">üìã</span>
            <h3>Smart Summaries</h3>
            <p>Key insights and next steps identified</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AudioUploader;
